package com.oracle.sim.pages.Admin;

import org.openqa.selenium.By;

import com.oracle.sim.pages.Base.SimBasePage;

public class ItemLookupPage extends SimBasePage {
	public ItemLookupPage() throws Exception {
		// TODO Auto-generated constructor stub
		super();
		addObject("Title",By.id("sim-screen-title"));
		addObject("SearchIcon",By.xpath("//oj-button[@id='search-button']"));
		addObject("ItemDetailTitle",By.xpath("//h3[contains(text(),'Item Detail:')]"));
		addObject("TabMessage",By.xpath("//h5[contains(text(),'No information available to display.')]"));
		addObject("ItemFilter",By.xpath("//input[contains(@id,'input-column-filter-id')]"));
		addObject("RecordsFoundText",By.xpath("//span[contains(text(),'Records Found:')]"));
		addObject("GridRecord",By.xpath("//mark[contains(@class,'grid-highlight')]"));
		addObject("PackItemsTab",By.xpath("//span[text()='Pack Items']"));
		addObject("FilterPackType",By.xpath("//input[contains(@id,'input-column-filter-itemType')]"));
		addObject("GridRecordPackType",By.xpath("//tr[1]//td[contains(@id,'-col-itemType')]"));
	
	
		addObject("ItemLookUpOption",By.xpath("//span[text()='Item Lookup']"));
		addObject("BrandDescription",By.xpath("//span[text()='Brand Description']"));
		addObject("BrandDescriptionTextBox",By.xpath("//input[@id='input-filter-brand-desc|input']"));
		addObject("ItemIdTextBox",By.xpath("//input[@id='input-filter-item|input']"));
		addObject("SearchHeading",By.xpath("//oj-button[@id='search-button']//div[@class='oj-button-label']"));
		
	
	
	}
}
